#!/usr/bin/python3
######## use this block to send message to V2X hub##################
####### The messages hit MessageReceiver Plugin first at port: 26789 ########

import socket
import binascii as ba
import time

udpip = "127.0.0.1"
udpport = 26789


sk = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)

# Send sample MobilityOperation messages
staging_area_messages = [
    {
        "DOT-80550: Arrival at ENTER_STAGING_AREA location": ""
    },
    {
        "DOT-80550: Arrive at PICKUP location": ""
    },
    {
        "DOT-80550: Arrive at EXIT_STAGING_AREA location": ""
    },
]

port_area_messages = [
    {
        "DOT-80550: Arrive at ENTER_PORT location": ""
    },
    {
        "DOT-80550: Arrive at DROPOFF location": ""
    },
    {
        "DOT-80550: DOT-80550: Arrive at PICKUP location": ""
    },
    {
        "DOT-80550: Arrive at PORT_CHECKPOINT location": ""
    },
    {
        "DOT-80550: Arrive at HOLDING_AREA location": ""
    },
    {
        "DOT-80550: Arrive at EXIT_PORT location": ""
    },
    {
        "DOT-80550: Arrive at ENTER_STAGING_AREA location": ""
    },
]


def send_messages(messages):
    for message in messages:
        title = list(message.keys())[0]
        message = list(message.values())[0]
        if "HOLDING" in title:
            hold = input("Move to holding location? (y/n): ")
            if hold != "y":
                continue
        unhexed = ba.unhexlify(message)
        sk.sendto(unhexed, (udpip, udpport))
        print(f"\n{title}. Sent: {message}")
        input("Press enter to send next arrival message.")


if __name__ == "__main__":
    while True:
        print("\nAvailable area options:\n\t1: staging\n\t2: port\n\t0: exit\n")
        area = input("Choose area: ")
        if area == "1":
            send_messages(staging_area_messages)
        elif area == "2":
            send_messages(port_area_messages)
        elif area == "0":
            break
        else:
            print("Invalid area. Choose 0 to exit")
